import React from 'react'
import styles from '../styles/ContactForm.module.css'
import firebase from '../server/firebase'

import {
        Formik,
        Form,
        Field,
        ErrorMessage,
      } from 'formik'
import * as Yup from 'yup'



      const initialValues = {
        Name: 'ajkn',
        Email: '',
        PhoneNumber: '',
        Region: '',
        Service: ''
        
      }
    
const validationSchema = Yup.object({
    
        Name: Yup.string().required('Please Enter Parent Name'),
        Email: Yup.string()
          .email('Invalid email format')
          .required('Email Required'),
        PhoneNumber: Yup.string().matches(/^[6-9]\d{9}$/, {message: "Please enter valid number.", excludeEmptyString: false})

      
      })      
const ContactForm = () => {
        
        async function registerUser(values){
                console.log('Form data', values)
                console.log('form  submit start')
                // const db = firebase.firestore(); 
                // db.collection("studentdb").add({
                //       parentname: "parengtname",
                //       username: "childname",
                //       userType: "student"
                //      })
                //      .then(function() {
                //       console.log("Document successfully written!");
                //      })
                //     .catch(function(error) {
                //       console.error("Error writing document: ", error);
                //      });    
          
                     console.log('form  submit end')
              }
    return (
            
            <Formik
                initialValues={ initialValues}
                validationSchema={validationSchema}
                onSubmit = {(values, {setSubmitting}) => {
                        registerUser(values);
                        setSubmitting(false);
                }}
            >
            
            {({ isSubmitting, isValid }) => (
                                <div className={styles.contactForm}>
                                <Form   className={styles.form}>

                                <h1 style={{margin: "0px 0px"}}>contatct Us</h1>

                                <label htmlFor="Name" className={styles.formLabel}>
                                        <span className={styles.contentName} >Name</span>
                                </label>
                                <Field className={styles.formInput} type='text'  id='Name' name='Name' />
                                <ErrorMessage name='Name' /> 

                                <label htmlFor="Email" className={styles.formLabel}>
                                        <span className={styles.contentName} >Email</span>
                                </label>
                                <Field className={styles.formInput} type='text'  id='Email' name='Email' />
                                <ErrorMessage name='Email' /> 

                                <label htmlFor="PhoneNumber" className={styles.formLabel}>
                                        <span className={styles.contentName} >Phone Number</span>
                                </label>
                                <Field className={styles.formInput} type='tel'  id='PhoneNumber' name='PhoneNumber' />
                                <ErrorMessage name='PhoneNumber' /> 
                                
                                <button
                                 disabled={!isValid || isSubmitting}
                                 type="submit"
                                >SUBMIT</button>
                                </Form>
                       
                        
                        </div>
            )}         
                
            </Formik>
                
    )
}

export default ContactForm
