import React, { Component } from "react";
import Slider from "react-slick";

import styles from '../styles/Reviews.module.css'
export default class Reviews extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      className: "slides"
    };
    return (
      <div className={styles.container}>
           <div className={styles.subcontainer} >
        <h2> Single Item</h2>
        <Slider className={styles.slider} {...settings}>
          <div >
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>
      </div>
   
      </div>
    );
  }
}

<style jsx>
  {`
   .slick-arrow .slick-next{
      background-color: black;
      color: black;

  }

  body{
    background: #09d3ac;
  }
   
  .slides .slick-prev,
  .slides .slick-next {
    position: absolute;
    top: 50%;
    z-index: 1;
  }
  
  
  .slides {
    position: relative;
  }
  
  .slides .slick-prev,
  .slides .slick-next {
    position: absolute;
    top: 50%;
    z-index:1;
  }
  
  .slides .slick-prev {
    left: 5%;
  }
  
  .slides .slick-next {
    right: 5%;
  }
  
  
  `}
</style>
