import React , {useState} from 'react'
import styles from '../styles/ContactForm.module.css'
import firebase from '../server/firebase'
import Select from './Select'
import {
        Formik,
        Form,
        Field,
        ErrorMessage,
      } from 'formik'
import * as Yup from 'yup'

      

      const initialValues = {
        Name: 'ajkn',
        Email: 's@s.com',
        PhoneNumber: '9999999999',
        Place: '',
        Service: ''
        
      }
    
const validationSchema = Yup.object({
    
        Name: Yup.string().required('Please Enter Parent Name'),
        Email: Yup.string()
          .email('Invalid email format')
          .required('Email Required'),
        PhoneNumber: Yup.string().matches(/^[6-9]\d{9}$/, {message: "Please enter valid number.", excludeEmptyString: false})
                        .required('Phone number is required'),
        Place: Yup.string().required('Please Choose a place'),
        Service: Yup.string().required('Please Choose a place')
      
      })  
      const placeOptions = [
              {key: 'Select a Place', value: ''},
              {key: 'Place1', value: 'Place1'},
              {key: 'Place2', value: 'Place2'},
              {key: 'None', value: 'None'},
              
         ]
         
         const serviceOptions = [
                {key: 'Select a Place', value: ''},
                {key: 'Place1', value: 'Place1'},
                {key: 'Place2', value: 'Place2'},
                {key: 'None', value: 'None'},
                
           ]
        
         
const ContactForm = () => {
        const [success, setSuccess] = useState(false)
        async function registerUser(values, {setSubmitting,resetForm}){
                console.log('Form data', values)
                console.log('form  submit start')
                
                const db = firebase.firestore(); 
                await db.collection('usersdb').add({
                        Name: values.Name,
                        Email: values.Email,
                        PhoneNumber: values.PhoneNumber,
                        Place: values.Place,
                        Service: values.Service,
                        dateCreated: new Date(),
                        
                     })
                     .then(function() {
                       resetForm()
                       setSubmitting(false);
                       setSuccess(true)
                     })
                    .catch(function(error) {
                     // console.error("Error writing document: ", error);
                     });    
                     
                     console.log('form  submit end')
              }
    return (
            
            <Formik
                initialValues={ initialValues}
                validationSchema={validationSchema}
                onSubmit = {(values, {setSubmitting,resetForm  }) => {
                       setSuccess(false);
                        registerUser(values , {setSubmitting,resetForm });
                        
                        
                        
                }}
                
            >
            
            {({ isSubmitting, isValid  }) => (
                                
                                <div className={styles.contactForm}>
                                     
                                  {  success ? <p className={styles.succesmsg}> Form Submission successful ✔️</p> : null }
                                { isSubmitting ?  <div className={styles.loader}> </div>  : 
                                <Form   className={styles.form}>
                                <h1 style={{margin: "0px 0px"}}>Contact Us</h1>

                                <label htmlFor="Name" className={styles.formLabel}>
                                        <span className={styles.contentName} >Name</span>
                                </label>
                                <Field className={styles.formInput} type='text'  id='Name' name='Name' />
                                <p className={styles.ErrorMessage}><ErrorMessage  name='Name' /> </p>
                                

                                <label htmlFor="Email" className={styles.formLabel}>
                                        <span className={styles.contentName} >Email</span>
                                </label>
                                <Field className={styles.formInput} type='text'  id='Email' name='Email' />
                                <p className={styles.ErrorMessage}><ErrorMessage   name='Email' /></p> 

                                <label htmlFor="PhoneNumber" className={styles.formLabel}>
                                        <span className={styles.contentName} >Phone Number</span>
                                </label>
                                <Field className={styles.formInput} type='tel'  id='PhoneNumber' name='PhoneNumber' />
                                <p className={styles.ErrorMessage}><ErrorMessage  name='PhoneNumber' /> </p>

                                <label htmlFor="Place" className={styles.formLabel}>
                                        <span className={styles.contentName} >Select A Place</span>
                                </label>

                                <Select
                                        
                                        name='Place'
                                        options={placeOptions} 
                                        className={styles.selectInput}
                                        
                                />
                                <p className={styles.ErrorMessage}><ErrorMessage  name={'Place'} /></p>
                                <label htmlFor="Service" className={styles.formLabel}>
                                        <span className={styles.contentName} >Select A Service</span>
                                </label>                              
                                
                                <Select
                                        
                                        name='Service'
                                        options={serviceOptions} 
                                        className={styles.selectInput}
                                        
                                />
                                <p className={styles.ErrorMessage}><ErrorMessage   name={'Service'} /> </p>

                                
                                
                                
                                <button
                                 disabled={!isValid || isSubmitting}
                                 type="submit"
                                 className={ styles.btnHover }
                                 style={ !isValid || isSubmitting ? { type: 'disabled' , background: '#dddddd' , color: 'black'} : {}}
                                >SUBMIT</button>
                                </Form>

                                
                       
            }
                        </div>
            )}        
                
            </Formik>
                
    )
}

export default ContactForm
