import React, { useState, useEffect } from "react";
import ReactMapGL, { Marker, Popup } from "react-map-gl";
import styles from '../styles/Information.module.css'
import ContactForm from "./ContactForm";

function Information() {
  const [viewport, setViewport] = useState({
    latitude: 17.388815,
    longitude: 78.491312,
    width: "50vw",
    height: "50vh",
    zoom: 15
  });
  const [selectedPark, setSelectedPark] = useState(null);

  useEffect(() => {
    const listener = e => {
      if (e.key === "Escape") {
        setSelectedPark(null);
      }
    };
    window.addEventListener("keydown", listener);

    return () => {
      window.removeEventListener("keydown", listener);
    };
  }, []);

  return (
    <div className={styles.informationContainer}>
        <div className={styles.map}>
      <ReactMapGL
        {...viewport}
        // mapboxApiAccessToken={process.env.REACT_APP_MAP_TOKEN}
        onViewportChange={viewport => {
          setViewport(viewport);
        }}
      >
      
          <Marker
           
            latitude={17.388815}
            longitude={78.491312}
          >
            <button
              className="marker-btn"
              onClick={e => {
                e.preventDefault();
                setSelectedPark(viewport);
              }}
            >
              <img src="/img/map.png" alt="Skate Park Icon"  style={{height:"10px",width: "10px"}}/>
            </button>
          </Marker>
    

        {selectedPark ? (
          <Popup
            latitude={viewport.latitude}
            longitude={viewport.longitude}
            onClose={() => {
              setSelectedPark(null);
            }}
          >
            <div>
              <h2>sri-global</h2>
              <p>home care</p>
            </div>
          </Popup>
        ) : null}
      </ReactMapGL>
      <h1>
      Sri Global Home Care Services
      </h1>
      <p> No.3-3-173 and 173/1, Chappal Bazar, Kachiguda, Hyderabad - 500027, RelianceDigital Back Side</p>
      </div>
        <div className={styles.formContainer}>
            <ContactForm/>
        </div>
    </div>
  );
}

export default Information;
