import React from 'react'
import styles from '../styles/ContactForm.module.css'
import firebase from '../server/firebase'

import {
        Formik,
        Form,
        Field,
        ErrorMessage,
      } from 'formik'
import * as Yup from 'yup'



      const initialValues = {
        Name: '',
        Email: '',
        PhoneNumber: '',
        Region: '',
        Service: ''
        
      }
    
const validationSchema = Yup.object({
    
        Name: Yup.string().required('Please Enter Parent Name'),
        Email: Yup.string()
          .email('Invalid email format')
          .required('Email Required'),
        PhoneNumber: Yup.number().required('Please Enter Your Phone Number'),

      
      })      
const ContactForm = () => {

        async function registerUser(values){
                console.log('Form data', values)
                console.log('form  submit start')
                // const db = firebase.firestore(); 
                // db.collection("studentdb").add({
                //       parentname: "parengtname",
                //       username: "childname",
                //       userType: "student"
                //      })
                //      .then(function() {
                //       console.log("Document successfully written!");
                //      })
                //     .catch(function(error) {
                //       console.error("Error writing document: ", error);
                //      });    
          
                     console.log('form  submit end')
              }



    return (
            <div className={styles.contactForm}>

                <div className={styles.form}>
                <h1 style={{margin: "0px 0px"}}>contatct Us</h1>
                <label htmlFor="name" className={styles.formLabel}>
                        <span className={styles.contentName} >Name</span>
                </label>
                <input type="text" className={styles.formInput} name="name" autoComplete="off" required/>

                <label htmlFor="email" className={styles.formLabel}>
                        <span className={styles.contentName} >Email</span>
                </label>
                <input type="email" className={styles.formInput} name="email" autoComplete="off" required/>
                <label htmlFor="phone" className={styles.formLabel}>
                        <span className={styles.contentName} >Phone Number</span>
                </label>
                <input type="tel" className={styles.formInput} name="email"  autoComplete="off" required pattern="[789][0-9]{9}"></input>
                <label htmlFor="place" className={styles.formLabel}>
                        <span className={styles.contentName} >select your region</span>
                </label>
                
                <select id="place"  className={styles.formInput} name="place">
                    <option value="volvo">Volvo</option>
                    <option value="saab">Saab</option>
                    <option value="fiat">Fiat</option>
                    <option value="audi">Audi</option>
                </select>

                <label htmlFor="place" className={styles.formLabel}>
                        <span className={styles.contentName} >select your Service</span>
                </label>
                
                <select id="place"  className={styles.formInput} name="place">
                    <option value="volvo">Volvo</option>
                    <option value="saab">Saab</option>
                    <option value="fiat">Fiat</option>
                    <option value="audi">Audi</option>
                </select>
                <button type="submit" onClick = { () => {registerUser()}} className={styles.btnHover}>SUBMIT</button>
                </div>
            </div>
           
    )
}

export default ContactForm
