import React from 'react'
import styles from '../styles/Reviews.module.css'
import Slider from "react-slick";
const Reviews = () => {

    let settings = {
        infinite: false,
        speed: 1000,
        arrows: true,
        slidesToShow: 2,
        slidesToScroll: 2,
    
        responsive: [
          {
            breakpoint: 960,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 2,
            },
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 2,
            },
          },
        ],
      };
    return (
      <div className="container">
      <h6 className="text-muted">Friend Suggestions</h6>
      {suggestions.length === 0 ? (
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      ) : (
        <Slider {...settings}>
          {suggestions.map((current) => (
            <div className="out" key={current.id}>
              <div className="card">
                <img
                  className="rounded-circle"
                  alt={"users here"}
                  src={`https://source.unsplash.com/random/${current.id}`}
                  height={56}
                  width={56}
                />
                <div className="card-body">
                  <h5 className="card-title">{current.username}</h5>
                  <small className="card-text text-sm-center text-muted">
                    In your contacts
                  </small>
                  <br />
                  <button className="btn btn-sm follow btn-primary">
                    Follow
                  </button>
                </div>
              </div>
            </div>
          ))}
        </Slider>
      )}
    </div>
      )
}

<style jsx>{
  `

  .card{
    text-align: center;
    padding-top: 10px;
    opacity: 1;
    width: 200px;
    color: white;
}

.card-title{
    color: #262626;
    display: block;
    overflow: hidden;
    font-size: 14px;
    line-height: 18px;
    text-overflow: ellipsis;
    white-space: nowrap;
    margin-bottom: 0 !important;
}

.container{
    margin-top: 100px;
}
img {
    display:block;
    margin:auto;
}
.slick-prev:before, .slick-next:before {
    color: black !important;
}




.follow{
    background-color: #3897f0 !important;
    border: 1px solid #3897f0 !important;
    border-radius: 4px;
    color: #fff;
    margin-top: 20px !important;
}
  `
}

</style>

export default Reviews
