import React from 'react'
import styles from '../styles/Reviews.module.css'
import Slider from "react-slick";
const Reviews = () => {

    let settings = {
        infinite: false,
        speed: 1000,
        arrows: true,
        slidesToShow: 2,
        slidesToScroll: 2,
    
        responsive: [
          {
            breakpoint: 960,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 2,
            },
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 2,
            },
          },
        ],
      };
    return (
        <div>
            <Slider {...settings}>
            
              <div className={styles.card}>
                
                <div className={styles.cardBody}>
                    <h1>jhsdbjhdbb</h1>
                </div>
              </div>
              <div className={styles.card}>
                
                <div className={styles.cardBody}>
                    <h1>jhsdbjhdbb</h1>
                </div>
              </div>
              <div className={styles.card}>
                
                <div className={styles.cardBody}>
                    <h1>jhsdbjhdbb</h1>
                </div>
              </div>
              <div className={styles.card}>
                
                <div className={styles.cardBody}>
                    <h1>jhsdbjhdbb</h1>
                </div>
              </div>
              <div className={styles.card}>
                
                <div className={styles.cardBody}>
                    <h1>jhsdbjhdbb</h1>
                </div>
              </div>
              <div className={styles.card}>
                
                <div className={styles.cardBody}>
                    <h1>jhsdbjhdbb</h1>
                </div>
              </div>
                
            </Slider>
            <h1>jshasdh</h1>
        </div>
    )
}

<style jsx>{
  `

  .card{
    text-align: center;
    padding-top: 10px;
    opacity: 1;
    width: 200px;
    color: white;
}

.card-title{
    color: #262626;
    display: block;
    overflow: hidden;
    font-size: 14px;
    line-height: 18px;
    text-overflow: ellipsis;
    white-space: nowrap;
    margin-bottom: 0 !important;
}

.container{
    margin-top: 100px;
}
img {
    display:block;
    margin:auto;
}
.slick-prev:before, .slick-next:before {
    color: black !important;
}




.follow{
    background-color: #3897f0 !important;
    border: 1px solid #3897f0 !important;
    border-radius: 4px;
    color: #fff;
    margin-top: 20px !important;
}
  `
}

</style>

export default Reviews
